// SudokuBoard.js
import React, { Component } from "react";
import SudokuCell from "./SudokuCellComponent";
import axios from "axios";
import SudokuRow from "./SudokuRowComponent";

class SudokuBoard extends Component {
    state = {details: [], board: [], }

    constructor(props) {
        super(props);

        let data_game;

        axios.get('http://localhost:8000')
            .then(res => {
                const jsonData = res.data;
                data_game = res.data;
                const resultArray = [];

                    for (let i = 1; i <= 9; i++) {
                        const rowKey = 'row' + i;
                        const rowData = jsonData[rowKey];

                        const rowArray = rowData.split(",").map(cell => {
                            return parseInt(cell, 10);
                        });

                        resultArray.push(rowArray);
                    }


                    this.setState({
                        details: data_game,
                        board: resultArray
                    });
                })
            .catch(err => {
                "Error mounting data"
            })


                //this.state.board = [
                //[5, 3, "", "", 7, "", "", "", ""]
                //[6, "", "", 1, 9, 5, "", "", ""],
                //["", 9, 8, "", "", "", "", 6, ""],
                //[8, "", "", "", 6, "", "", "", 3],
                //[4, "", "", 8, "", 3, "", "", 1],
                //[7, "", "", "", 2, "", "", "", 6],
                //["", 6, "", "", "", "", 2, 8, ""],
                //["", "", "", 4, 1, 9, "", "", 5],
                //["", "", "", "", 8, "", "", 7, 9],
            //]
        //};


    }

    handleCellChange = (row, col, value) => {
        const newBoard = [...this.state.board];
        newBoard[row][col] = value;
        this.setState({ board: newBoard });
    };

    render() {
        return (
            <div className="sudoku-board">
                {this.state.details.map((elem, index) => (
                    <div key={1} className="sudoku-row">
                        <h1>{elem.row1}</h1>
                    </div>
                ))}
            </div>
        );
    }
}

export default SudokuBoard;
