// SudokuBoard.js
import React, { Component } from "react";
import SudokuCell from "./SudokuCellComponent";
import axios from "axios";

class SudokuBoard extends Component {
    state = {details: [], board: [], }

    constructor(props) {
        super(props);

        let data;
        axios.get('http://localhost:8000')
            .then(res => {
                data = res.data;
                this.setState({
                    d1: data.row1,
                    d2: data.row2,
                    d3: data.row3,
                    d4: data.row4,
                    d5: data.row5,
                    d6: data.row6,
                    d7: data.row7,
                    d8: data.row8,
                    d9: data.row9
                });
            })

        //this.state = {
            //board: [
                //[5, 3, "", "", 7, "", "", "", ""],
                //[6, "", "", 1, 9, 5, "", "", ""],
                //["", 9, 8, "", "", "", "", 6, ""],
                //[8, "", "", "", 6, "", "", "", 3],
                //[4, "", "", 8, "", 3, "", "", 1],
                //[7, "", "", "", 2, "", "", "", 6],
                //["", 6, "", "", "", "", 2, 8, ""],
                //["", "", "", 4, 1, 9, "", "", 5],
                //["", "", "", "", 8, "", "", 7, 9],
            //],
        //};

            .catch(err => {
                "Error mounting data"
            })
    }

    handleCellChange = (row, col, value) => {
        const newBoard = [...this.state.board];
        newBoard[row][col] = value;
        this.setState({ board: newBoard });
    };

    render() {
        return (
                <div className="sudoku-board">
                        <div key={1} className="sudoku-row">
                            {this.state.d1.map((cell, colIndex) => (
                                <SudokuCell
                                    key={colIndex}
                                    value={cell}
                                    onChange={(value) =>
                                        this.handleCellChange(1, colIndex, value)
                                    }
                                />
                            ))}
                        </div>
                </div>
        );
    }
}

export default SudokuBoard;
