// SudokuBoard.js
import React, { Component } from "react";
import SudokuCell from "./SudokuCellComponent";
import axios from "axios";

class SudokuBoard extends Component {
    state = {details: [], board: [], }

    constructor(props) {
        super(props);

        let data_game;

        axios.get('http://localhost:8000')
            .then(res => {
                data_game = res.data;
                this.setState({
                    details: data_game
                });
            })
            .catch(err => {
                "Error mounting data"
            })


            this.state.board.append(this.state.details['row1']);

            //this.state.board = [
                //[5, 3, "", "", 7, "", "", "", ""]
                //[6, "", "", 1, 9, 5, "", "", ""],
                //["", 9, 8, "", "", "", "", 6, ""],
                //[8, "", "", "", 6, "", "", "", 3],
                //[4, "", "", 8, "", 3, "", "", 1],
                //[7, "", "", "", 2, "", "", "", 6],
                //["", 6, "", "", "", "", 2, 8, ""],
                //["", "", "", 4, 1, 9, "", "", 5],
                //["", "", "", "", 8, "", "", 7, 9],
            //]
        //};


    }

    handleCellChange = (row, col, value) => {
        const newBoard = [...this.state.board];
        newBoard[row][col] = value;
        this.setState({ board: newBoard });
    };

    render() {
        return (
            <div className="sudoku-board">
                    <div key={1} className="sudoku-row">
                        {this.state.board.map((output, id) =>
                            <SudokuCell
                                key={id}
                                value={output}
                                onChange={(value) =>
                                    this.handleCellChange(id, 1, value)
                                }
                            />
                            )}
                    </div>
            </div>
        );
    }
}

export default SudokuBoard;
